package com.createiq;

public class EqualsAndEqualsIgnoreCase2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String input = "ROSE";
		 
		if(input.equalsIgnoreCase("rose")) {
			System.out.println("Jasmine");
		}
		if(input.equalsIgnoreCase("marigold")) {
			System.out.println("lily");
		}
		if(input.equalsIgnoreCase("tulip")) {
			System.out.println("sunflower");
		}
		if(input.equalsIgnoreCase("lotus")) {
			System.out.println("daisy");
		}
		if(input.equalsIgnoreCase("hibiscus")) {
			System.out.println("primrose");
		}
		if(input.equalsIgnoreCase("orchid")) {
			System.out.println("rohira");
		}

	}

}
