package com.createiq.operators;

public class BitwiseOperator2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 4;
		int b = 5;
		
		 int c = a&b;
		 int d = a|b;
		 int e = a^b;
		
	System.out.println("a&b: "+c);
	System.out.println("a|b: "+d);
	System.out.println("a^b: "+e);
	   
		

	}

}
