package com.createiq.operators;

public class LogicalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a=4;
       int b=7;
        
       System.out.println((a<b)&&(b>a));
       System.out.println((a>b)||(a<b));
       System.out.println((a>b)^(a>b));
       
	}

}
