package com.createiq.operators;

public class ternaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y;  
		x = 20;  
		y = (x == 1) ? 61: 90;  
		System.out.println("Value of y is: " +  y);  
		y = (x == 20) ? 61: 90;  
		System.out.println("Value of y is: " + y);  
		
	
	}

}
